(function ($) {

Drupal.behaviors.gzr = {};
Drupal.behaviors.gzr.attach = function(context) {
  var $main = $('#main');

  // Add masonry to portfolio page.
  var $container = $main.find('.pane-contributions-panel-pane-1');
  $container.imagesLoaded(function(){
    $container.masonry({
      itemSelector: '.views-row',
      columnWidth: 200,
    });
  });

  // Trigger hover on footer, when hovering over "Contact" menu item.
  $('#main-menu-links').find('li.menu-7651').hover(
   function () {
      $('footer').addClass("hover");
    },
    function () {
      $('footer').removeClass("hover");
    }
  );

  // Trigger hover on blog teaser, when touching it on touch-devices.
  $main.find('.pane-blog-panel-pane-1').on('touchstart', function() {
    $(this).addClass('hover');
  });

  var $cycle = $main.find('.pane-quote-panel-pane-1 .view-content');
  if ($cycle.length) {
    $cycle.cycle({
      fx: 'fade',
      slideResize: 0,
      slideExpr: '.views-row:not(\'.views-row-1\')',
    }).find('.views-row-1').remove();
  }


};

})(jQuery);
;
